mapper.insert:
  		INSERT INTO tbl_board (
				bno, title, content, writer, regdate, updatedate
			) VALUES (
				(select nextval(seq))
				, #{title}
				, #{content}
				, #{writer}
				, SYSDATE()
				, SYSDATE()
			)

mapper.update:
UPDATE tbl_board SET 
	title = #{title}
  , content = #{content}
  , writer = #{writer}
  , updatedate = SYSDATE()
WHERE bno = #{bno}
